<?php	header("Content-Type:text/html; charset=utf-8");	include('./db.php');		$master_ary = array();	// 取得神人資訊	$query = sprintf( "SELECT USERID,USERNAME,EMAIL,GENDER,DEPARTMENT,JOIN_TIME,SKILL,MOTTO,NEED,ABOUT_ME,EXPERIENCE,LASTUSING_TIME,SCORE,USERIP,USER_PHOTO FROM `1_CV` WHERE IS_MASTER = '1' ORDER BY SCORE DESC" );	$result = mysql_query($query) or die('error@取得神人資訊錯誤。');	while( $a = mysql_fetch_array($result) ){		$master_ary[] = $a['USERID'].'***'.$a['USERNAME'].'***'.$a['EMAIL'].'***'.$a['GENDER'].'***'.$a['DEPARTMENT'].'***'.$a['JOIN_TIME'].'***'.$a['SKILL'].'***'.$a['MOTTO'].'***'.$a['NEED'].'***'.$a['ABOUT_ME'].'***'.$a['EXPERIENCE'].'***'.$a['LASTUSING_TIME'].'***'.$a['SCORE'].'***'.$a['USERIP'].'***'.$a['USER_PHOTO'];	}	echo 'success@@'.json_encode( (object)$master_ary );?>